import React, { useState, useEffect } from "react";
import axios from 'axios';

var arrobjs = [];



const Log = () => {

  const [logs, setLogs] = useState([]);
  const [time, setTime] = useState([]);
  const [isRunning, setIsRunning] = useState(false);
  const [intervalId, setIntervalId] = useState();

  useEffect(() => {
    if(isRunning){
    const id = window.setInterval(() => {
      console.log('this will run every two secs!');
      axios.get('https://join.reckon.com/stock-pricing')
        .then(res => {
          console.log(res.data);
          var currentdate = new Date();
          setTime("Updates for " + currentdate.getDate() + "/"
          + (currentdate.getMonth()+1)  + "/" 
          + currentdate.getFullYear() + "  "  
          + currentdate.getHours() + ":"  
          + currentdate.getMinutes() + ":" 
          + currentdate.getSeconds());
          res.data.map(item =>  arrobjs.push(item));
          console.log("you have reached here");
          setLogs(arrobjs.reverse());
        })
        .catch(err => {
          console.log(err)
        })
    }, 2000);
    setIntervalId(intervalId);
  }
  else{
    window.clearInterval(intervalId);
  }
}, [isRunning]);

  return (

    <div className="log-box">
      <h1>Log</h1>
      <button onClick ={() => {
        setIsRunning(true);}}>Pause Log</button>
      <h4>{time}</h4>
      <ol>
        {
          Object.keys(logs)
            .map((item, i) => (

              <li key={i}>

                <span > {logs[item].code}: ${logs[item].price} </span>
              </li>))}

      </ol>
    </div>
  );
};

export default Log

