import React, { useState, useEffect } from "react";
import axios from 'axios';

var arrobjs = [];
var time = "0";


const Log = () => {

  const [logs, setLogs] = useState([]);
  //const [time, setTime] = useState([]);
  const [isRunning, setIsRunning] = useState(true);
  const [intervalId, setIntervalId] = useState(null);

  useEffect(() => {
    if(isRunning){
    const id = window.setInterval(() => {
      console.log('this will run every two secs!');
      axios.get('https://join.reckon.com/stock-pricing')
        .then(res => {
          console.log(res.data);
          var currentdate = new Date();
          time = "Updates for " + currentdate.getDate() + "/"
          + (currentdate.getMonth()+1)  + "/" 
          + currentdate.getFullYear() + "  "  
          + currentdate.getHours() + ":"  
          + currentdate.getMinutes() + ":" 
          + currentdate.getSeconds();
          res.data.update = time;
          console.log(res.data);
          res.data.map(item =>  arrobjs.push(item));
          console.log("you have reached here");
          setLogs(arrobjs.reverse());
        })
        .catch(err => {
          console.log(err)
        })
    }, 20000);
    return () => window.clearInterval(id);
  }
}, [isRunning]);

  return (

    <div className="log-box">
      <h1>Log</h1>
      <button id="btn" onClick ={() => { 
        setIsRunning(!isRunning);
          if(isRunning === true){
            document.getElementById("btn").innerHTML ="Resume Log";
          } 
          else{document.getElementById("btn").innerHTML ="Pause Log";}
         }}>Pause Log</button>
      <h4>{time}</h4>
      <ol>
        {
          Object.keys(logs)
            .map((item, i) => (

              <li key={i}>

                <span > {logs[item].code}: ${logs[item].price} </span>
              </li>))}

      </ol>
    </div>
  );
};

export default Log

