const Log = () => {

   const [logs, setLogs] = useState([]);
   //const [update, setUpdate] = useState({});

   useEffect(() => {
     const interval = setInterval(() => {
       console.log ('this will run every two secs!');

    axios.get('https://join.reckon.com/stock-pricing')
      .then(res => {


        var today = new Date();
        res.data.time = today.getHours() + ":" + today.getMinutes() + ":" + today.getSeconds();
        arrobjs.push(res.data);
        setLogs(arrobjs);
        console.log(arrobjs);
      })
      .catch(err => {
        console.log(err)
      })
    },10000);
    return () => clearInterval(interval);
  },[]);

  return (
    <div className ="log-box">
      <h3>Last Update: {logs.time}</h3>
      <ul>
        {
          logs.forEach(l => {
            l.forEach( ll => {
              <li>
              <span >{ ll.code }: ${ll.price}  </span>
                </li>
            })
          })


        }

      </ul>
    </div>
  );
};

export default Log

