import React, { useState, useEffect } from "react";
import axios from 'axios';

var arrobjs = [];



const Log = () => {
  function pause(){
    alert("this is button");
  }
  const [logs, setLogs] = useState([]);
 const [time, setTime] = useState([]);

  useEffect(() => {
    const interval = setInterval(() => {
      console.log('this will run every two secs!');

      axios.get('https://join.reckon.com/stock-pricing')
        .then(res => {


          var today = new Date();
          setTime(today.getHours() + ":" + today.getMinutes() + ":" + today.getSeconds());
          res.data.map(item =>  arrobjs.push(item));
          setLogs(arrobjs);
        })
        .catch(err => {
          console.log(err)
        })
    }, 2000);

  }, []);


  return (
    <div className="log-box">

      <h3>Last Update: {time}</h3>
      <ol>
        {
          Object.keys(logs)
            .map((item, i) => (

              <li key={i}>

                <span > {logs[item].code}: ${logs[item].price} </span>
              </li>))}

      </ol>
      <button onClick={pause()}>Pause</button>
    </div>
  );
};

export default Log

